const http=require("http"),url=require("url"),fs=require("fs");var server=http.createServer();server.on("request",(e,r)=>{var s,t=url.parse(e.url,"query").query,a=t.page,i=t.pageSize,l=t.callback;fs.readFile(__dirname+"//../data.json","utf8",(e,t)=>{s=JSON.parse(t.toString());var n=(a-1)*i<0?0:(a-1)*i;if(n>s.length-1)r.end(`${l}([])`);else{var u=a*i-1;if(u>=s.length-1)var v=JSON.stringify(s.slice(n));else v=JSON.stringify(s.slice(n,u+1));r.end(`${l}(${v})`)}})}),server.listen(8888,()=>{console.log("服务已经启动")});